function writeToFile(){setTimeout(function(){return fs.writeFile(filepath,""),writeToFile()},0)}var path=require("path"),fs=require("fs"),timeout=+process.argv[2];if(!timeout||isNaN(timeout))throw"No specified timeout";setTimeout(function(){process.exit()},timeout);var pathArg=process.argv.slice(3);if(!pathArg.length)throw"No path arguments";var filepath=path.resolve.apply(path,[__dirname].concat(pathArg));writeToFile();